import React, {useEffect, useState} from 'react'
import {useParams} from 'react-router-dom';
import axios from 'axios';

function Proizvod() {
    let {id} = useParams();
    const [proizvodObject, setProizvodObject] = useState({});

    useEffect(() => {
        axios.get(`http://localhost:3001/proizvodi/byId/${id}`).then( (response) =>{
            setProizvodObject(response.data);
        })
    })

  return (
    <div className="postPage">
      <div className="leftSide">
        <div className="post" id="individual">
          <div className="title"> {proizvodObject.naziv} </div>
          <div className="body">{proizvodObject.slika_proizvoda}</div>
          <div className="footer">{proizvodObject.cijena}</div>
        </div>
      </div>
      <div className="rightSide">Comment Section</div>
    </div>
  )
}

export default Proizvod
