import React, {useEffect, useState} from 'react'
import {useParams} from 'react-router-dom';
import axios from 'axios';

function ProizvodniProces() {
    let {id} = useParams();
    const [proizvodniProcesObject, setProizvodniProcesObject] = useState({});
    const [listOfPoizvodi, setListOfProizvodi] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:3001/proizvodniprocesi/byId/${id}`).then( (response) =>{
            setProizvodniProcesObject(response.data);
        });
        axios.get(`http://localhost:3001/proizvodi/${id}`).then((response) => {
            setListOfProizvodi(response.data);
          });
    });

  return (
    <div className="postPage">
      <div className="leftSide">
        <div className="post" id="individual">
          <div className="title"> {proizvodniProcesObject.naziv} </div>
          <div className="body">{proizvodniProcesObject.datum_pocetka}</div>
          <div className="footer">{proizvodniProcesObject.cijena}</div>
        </div>
      </div>
      <div className="rightSide">
        <div className="listOfComments">
          {listOfPoizvodi.map((proizvod, key) => {
            return (
              <div key={key} className="comment">
                {proizvod.naziv}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  )
}

export default ProizvodniProces
