import React from 'react';
import {Formik, Form, Field, ErrorMessage} from 'formik';
import * as Yup from 'yup';
import axios from "axios";
import {useNavigate} from 'react-router-dom';
import { AuthContext } from "../helpers/AuthContext";

function CreateProizvod() {

let navigate = useNavigate();
const { authState } = useContext(AuthContext);

    const initialValues = {
        naziv: "",
        slika_proizvoda: "",
        marza: "",
        cijena: "",
      };

      useEffect(() => {
        if (!localStorage.getItem("accessToken")) {
          navigate("/login");
        }
      }, []);

      const validationSchema = Yup.object().shape({
        naziv: Yup.string().required("Moras unijeti naziv!"),
        slika_proizvoda: Yup.string().required(),
        marza: Yup.number().required(),
        cijena: Yup.number().required(),
      });
    
      const onSubmit = (data) => {
        axios.post("http://localhost:3001/proizvodi", data).then((response) => {
          navigate("/");
        });
      };

  return (
    <div className="createPostPage">
      <Formik
      initialValues={initialValues}
      onSubmit={onSubmit}
      validationSchema={validationSchema}
      >
        <Form className="formContainer">
          <label>Naziv: </label>
          <ErrorMessage name="naziv" component="span" />
          <Field
            id="inputCreatePost"
            name="naziv"
            placeholder="naziv"
          />
          <label>Slika proizvoda: </label>
          <ErrorMessage name="slika_proizvoda" component="span" />
          <Field
            id="inputCreatePost"
            name="slika_proizvoda"
            placeholder="url slike proizvoda"
          />
          <label>Marza: </label>
          <ErrorMessage name="marza" component="span" />
          <Field
            id="inputCreatePost"
            type = "number"
            name="marza"
            placeholder="10"
          />
        <label>Cijena: </label>
          <ErrorMessage name="cijena" component="span" />
          <Field
            id="inputCreatePost"
            type = "number"
            name="cijena"
            placeholder="15.5"
          />
          <button type="submit"> Kreiraj proizvod</button>
        </Form>
      </Formik>
    </div>
  )
}

export default CreateProizvod
