import React, { useEffect, useState } from "react";
import {Formik, Form, Field, ErrorMessage} from 'formik';
import * as Yup from 'yup';
import axios from "axios";
import {useNavigate} from 'react-router-dom';

function CreateSirovina() {

const [listOfDobavljaci, setListOfDobavljaci] = useState([]);
let navigate = useNavigate();

    const initialValues = {
        naziv: "",
        kolicina: "",
        min_kolicina: "",
        cijena: "",
        jedinica_mjere: "",
        da_li_se_koristi: "",
        dobavljac_id: "",
      };

      useEffect(() => {
        if (!localStorage.getItem("accessToken")) {
          navigate("/login");
        } else{
            axios.get("http://localhost:3001/dobavljaci").then((response) =>{
              setListOfDobavljaci(response.data);
            });
        };
      }, []);

      const validationSchema = Yup.object().shape({
        naziv: Yup.string().required("Moras unijeti naziv!"),
        kolicina: Yup.number().required(),
        min_kolicina: Yup.number().required(),
      });
    
      const onSubmit = (data) => {
        axios.post("http://localhost:3001/sirovine", data).then((response) => {
          navigate("/");
        });
      };

  return (
    <div>
      <Formik
      initialValues={initialValues}
      onSubmit={onSubmit}
      validationSchema={validationSchema}
      >
        <Form className="formContainer">
          <label>Naziv: </label>
          <ErrorMessage name="naziv" component="span" />
          <Field
            id="inputCreatePost"
            name="naziv"
            placeholder="naziv"
          />
          <label>Kolicina: </label>
          <ErrorMessage name="kolicina" component="span" />
          <Field
            id="inputCreatePost"
            name="kolicina"
            placeholder="100"
            type="number"
          />
          <label>Minimalna kolicina: </label>
          <ErrorMessage name="min_kolicina" component="span" />
          <Field
            id="inputCreatePost"
            type = "number"
            name="min_kolicina"
            placeholder="10"
          />
        <label>Cijena: </label>
          <ErrorMessage name="cijena" component="span" />
          <Field
            id="inputCreatePost"
            name="cijena"
            placeholder="25"
          />
        <label>Jedinica mjere: </label>
          <ErrorMessage name="jedinica_mjere" component="span" />
          <Field
            id="inputCreatePost"
            name="jedinica_mjere"
            placeholder="v"
          />
        <label>Da li se koristi? </label>
          <ErrorMessage name="da_li_se_koristi" component="span" />
          <div className="radio">
            <label>
              <Field
                id="inputCreatePost"
                name="da_li_se_koristi"
                type="radio"
                value = "1"
              />Da
            </label>
            <label>
              <Field
                id="inputCreatePost"
                name="da_li_se_koristi"
                type="radio"
                value = "0"
              />Ne
            </label>
          </div>
        <label>Dobavljac: </label>
          <ErrorMessage name="dobavljac_id" component="span" />
          <Field 
            type = "select"
            name="dobavljac_id">
            {listOfDobavljaci.map((value, key) => {
              return(
                <option>{value.id}</option>
          );
        })}
          </Field>
          <button type="submit"> Dodaj dobavljaca</button>
        </Form>
      </Formik>
    </div>
  )
}

export default CreateSirovina
