import './App.css';
import {BrowserRouter as Router, Route, Link, Routes} from 'react-router-dom';
import Proizvodi from "./pages/Proizvodi";
import CreateProizvod from './pages/CreateProizvod';
import Proizvod from './pages/Proizvod';
import ProizvodniProcesi from './pages/ProizvodniProcesi';
import CreateProizvodniProces from './pages/CreateProizvodniProces';
import ProizvodniProces from './pages/ProizvodniProces';
import Login from "./pages/Login";
import Registration from "./pages/Registration";
import { AuthContext } from "./helpers/AuthContext";
import { useState, useEffect } from "react";
import axios from "axios";
import PageNotFound from "./pages/PageNotFound";

function App() {

  const [authState, setAuthState] = useState({
    korisnicko_ime: "",
    id: 0,
    uloga: "",
    status: false,
  });

  useEffect(() => {
    axios
      .get("http://localhost:3001/korisnici/auth", {
        headers: {
          accessToken: localStorage.getItem("accessToken"),
        },
      })
      .then((response) => {
        if (response.data.error) {
          setAuthState({ ...authState, status: false });
        } else {
          setAuthState({
            korisnicko_ime: response.data.korisnicko_ime,
            id: response.data.id,
            uloga: response.data.uloga,
            status: true,
          });
        }
      });
  }, []);

  const logout = () => {
    localStorage.removeItem("accessToken");
    setAuthState({ korisnicko_ime: "", id: 0, uloga: "", status: false });
  };


  return (
    <div className="App">
      <AuthContext.Provider value={{ authState, setAuthState }}>
        <Router>
          <div className='navbar'>
          <div className="links">
            <Link to='/proizvodi'>Proizvodi</Link>
            <Link to='/createproizvod'>Create a Proizvod</Link>
            <Link to='/proizvodniprocesi'>Proizvodni Procesi</Link>
            <Link to='/createproizvodniproces'>Create a Proizvodni Proces</Link>
            {!authState.status && (
                <>
                  <Link to="/login"> Login</Link>
                  <Link to="/registration"> Registration</Link>
                </>
              )}
            </div>
            <div className="loggedInContainer">
              <h1>{authState.korisnicko_ime} </h1>
              {authState.status && <button onClick={logout}> Logout</button>}
            </div>
            </div>
          <Routes>
            <Route path='/proizvodi' exact element={<Proizvodi />} />
            <Route path='/createproizvod' exact element={<CreateProizvod />} />
            <Route path='/proizvod/:id' exact element={<Proizvod />} />
            <Route path='/proizvodniprocesi' exact element={<ProizvodniProcesi />} />
            <Route path='/createproizvodniproces' exact element={<CreateProizvodniProces />} />
            <Route path='/proizvodniproces/:id' exact element={<ProizvodniProces />} />
            <Route path='/login' exact element={<Login />} />
            <Route path='/registration' exact element={<Registration />} />
            <Route path="*" exact element={PageNotFound} />
          </Routes>
        </Router>
      </AuthContext.Provider>
    </div>
  );
}

export default App;
