import React from 'react';
import {Formik, Form, Field, ErrorMessage} from 'formik';
import * as Yup from 'yup';

function CreateProizvod() {

    const initialValues = {
        naziv: "",
        slika_proizvoda: "",
        proizvodni_proces_id: "",
        marza: "",
        cijena: "",
      };

      const validationSchema = Yup.object().shape({
        naziv: Yup.string().required("Moras unijrti naziv!"),
        slika_proizvoda: Yup.string().required(),
        proizvodni_proces_id: Yup.number().required(),
        marza: Yup.number().required(),
        cijena: Yup.number().required(),
      });
    
      const onSubmit = (data) => {
       /* axios.post("http://localhost:3001/posts", data).then((response) => {
          console.log("IT WORKED");
        });*/
        console.log(data);
      };

  return (
    <div className="createPostPage">
      <Formik
      initialValues={initialValues}
      onSubmit={onSubmit}
      validationSchema={validationSchema}
      >
        <Form className="formContainer">
          <label>Naziv: </label>
          <ErrorMessage name="naziv" component="span" />
          <Field
            id="inputCreatePost"
            name="naziv"
            placeholder="naziv"
          />
          <label>Slika proizvoda: </label>
          <ErrorMessage name="slika_proizvoda" component="span" />
          <Field
            id="inputCreatePost"
            name="slika_proizvoda"
            placeholder="url slike proizvoda"
          />
          <label>Proizvodni proces: </label>
          <ErrorMessage name="proizvodni_proces_id" component="span" />
          <Field
            id="inputCreatePost"
            type = "number"
            name="proizvodni_proces_id"
            placeholder="1"
          />
          <label>Marza: </label>
          <ErrorMessage name="marza" component="span" />
          <Field
            id="inputCreatePost"
            type = "number"
            name="marza"
            placeholder="10"
          />
        <label>Cijena: </label>
          <ErrorMessage name="cijena" component="span" />
          <Field
            id="inputCreatePost"
            type = "number"
            name="cijena"
            placeholder="15.5"
          />
          <button type="submit"> Kreiraj proizvod</button>
        </Form>
      </Formik>
    </div>
  )
}

export default CreateProizvod
