import React, { useContext, useEffect } from "react";
import {Formik, Form, Field, ErrorMessage} from 'formik';
import * as Yup from 'yup';
import axios from "axios";
import {useNavigate} from 'react-router-dom';
import { AuthContext } from "../helpers/AuthContext";

function CreateProizvodniProces() {

let navigate = useNavigate();
const { authState } = useContext(AuthContext);

    const initialValues = {
        naziv: "",
        datum_pocetka: "",
        cijena: "",
      };

      useEffect(() => {
        if (!localStorage.getItem("accessToken")) {
          navigate("/login");
        }
      }, []);

      const validationSchema = Yup.object().shape({
        naziv: Yup.string().required("Moras unijeti naziv!"),
        datum_pocetka: Yup.date().required(),
        cijena: Yup.number().required(),
      });
    
      const onSubmit = (data) => {
        axios.post("http://localhost:3001/proizvodniprocesi", data).then((response) => {
          navigate("/");
        });
      };

  return (
    <div className="createPostPage">
      <Formik
      initialValues={initialValues}
      onSubmit={onSubmit}
      validationSchema={validationSchema}
      >
        <Form className="formContainer">
          <label>Naziv: </label>
          <ErrorMessage name="naziv" component="span" />
          <Field
            id="inputCreatePost"
            name="naziv"
            placeholder="naziv"
          />
          <label>Datum pocetka: </label>
          <ErrorMessage name="datum_pocetka" component="span" />
          <Field
            type="date"
            id="inputCreatePost"
            name="datum_pocetka"
            placeholder="datum pocetka"
          />
        <label>Cijena: </label>
          <ErrorMessage name="cijena" component="span" />
          <Field
            id="inputCreatePost"
            type = "number"
            name="cijena"
            placeholder="15.5"
          />
          <button type="submit"> Kreiraj proizvodni proces</button>
        </Form>
      </Formik>
    </div>
  )
}

export default CreateProizvodniProces
